import React, { useEffect, useState } from 'react';
import { getDiaryEntries, deleteDiaryEntry } from '../db';
import { Link } from 'react-router-dom';
import ExportButtons from './ExportButtons';
import { Document, Packer, Paragraph, HeadingLevel, ImageRun } from 'docx';

function DiaryList(props) {
  const [entries, setEntries] = useState([]);
  const [search, setSearch] = useState('');
  const [tagFilter, setTagFilter] = useState('');
  const [moodFilter, setMoodFilter] = useState('');

  useEffect(() => {
    getDiaryEntries().then(setEntries);
  }, [props.refresh]);

  // Filter entries
  const filtered = entries.filter(entry => {
    const matchesSearch = entry.title.toLowerCase().includes(search.toLowerCase()) || entry.content.toLowerCase().includes(search.toLowerCase());
    const matchesTag = tagFilter ? entry.tags.includes(tagFilter) : true;
    const matchesMood = moodFilter ? entry.mood === moodFilter : true;
    return matchesSearch && matchesTag && matchesMood;
  });

  // Download all entries as Word
  const downloadAllAsWord = async () => {
    const allEntries = await getDiaryEntries();
    const sections = await Promise.all(allEntries.map(async (entry) => {
      const children = [
        new Paragraph({
          text: entry.title,
          heading: HeadingLevel.HEADING_1,
        }),
        new Paragraph({ text: entry.date }),
        entry.tags && entry.tags.length > 0 ? new Paragraph({ text: 'Tags: ' + entry.tags.join(', ') }) : null,
        entry.mood ? new Paragraph({ text: 'Mood: ' + entry.mood }) : null,
        new Paragraph({ text: '' }),
      ].filter(Boolean);

      // Add main diary image if present
      if (entry.image) {
        let imgData;
        try {
          if (entry.image.startsWith('data:image')) {
            const base64 = entry.image.split(',')[1];
            const binary = atob(base64);
            const len = binary.length;
            const buffer = new Uint8Array(len);
            for (let i = 0; i < len; i++) {
              buffer[i] = binary.charCodeAt(i);
            }
            imgData = buffer;
          } else {
            imgData = await fetch(entry.image).then(r => r.arrayBuffer());
          }
          children.push(new Paragraph({
            children: [
              new ImageRun({
                data: imgData,
                transformation: { width: 400, height: 300 },
              })
            ]
          }));
        } catch (e) {
          // Ignore image if it fails to load
        }
      }

      // Parse HTML content for images and text
      const tempDiv = document.createElement('div');
      tempDiv.innerHTML = entry.content;

      async function htmlToDocxParagraphs(node) {
        let paragraphs = [];
        if (node.nodeType === Node.TEXT_NODE) {
          if (node.textContent.trim()) {
            paragraphs.push(new Paragraph({ text: node.textContent }));
          }
        } else if (node.nodeType === Node.ELEMENT_NODE) {
          if (node.tagName === 'IMG') {
            const src = node.getAttribute('src');
            if (src) {
              let imgData;
              if (src.startsWith('data:image')) {
                // Convert base64 to ArrayBuffer
                const base64 = src.split(',')[1];
                const binary = atob(base64);
                const len = binary.length;
                const buffer = new Uint8Array(len);
                for (let i = 0; i < len; i++) {
                  buffer[i] = binary.charCodeAt(i);
                }
                imgData = buffer;
              } else {
                imgData = await fetch(src).then(r => r.arrayBuffer());
              }
              paragraphs.push(new Paragraph({
                children: [
                  new ImageRun({
                    data: imgData,
                    transformation: { width: 400, height: 300 },
                  })
                ]
              }));
            }
          } else {
            // Handle formatting tags and recursively process children
            let paraOptions = {};
            switch (node.tagName) {
              case 'H1': paraOptions.heading = HeadingLevel.HEADING_1; break;
              case 'H2': paraOptions.heading = HeadingLevel.HEADING_2; break;
              case 'H3': paraOptions.heading = HeadingLevel.HEADING_3; break;
              case 'B':
              case 'STRONG': paraOptions.bold = true; break;
              case 'I':
              case 'EM': paraOptions.italics = true; break;
            }
            if (node.tagName === 'UL' || node.tagName === 'OL') {
              for (const li of node.children) {
                if (li.tagName === 'LI') {
                  paragraphs.push(new Paragraph({
                    text: li.textContent,
                    bullet: node.tagName === 'UL' ? { level: 0 } : undefined,
                    numbering: node.tagName === 'OL' ? { reference: 'numbered-list', level: 0 } : undefined,
                  }));
                }
              }
            } else {
              // For other tags, recursively process children and apply formatting
              let text = '';
              for (const child of node.childNodes) {
                if (child.nodeType === Node.TEXT_NODE) {
                  text += child.textContent;
                } else {
                  const childParagraphs = await htmlToDocxParagraphs(child);
                  paragraphs = paragraphs.concat(childParagraphs);
                }
              }
              if (text.trim()) {
                paragraphs.push(new Paragraph({ text, ...paraOptions }));
              }
            }
          }
        }
        return paragraphs;
      }

      for (const node of tempDiv.childNodes) {
        const nodeParagraphs = await htmlToDocxParagraphs(node);
        children.push(...nodeParagraphs);
      }

      return { children };
    }));
    const doc = new Document({ sections });
    const blob = await Packer.toBlob(doc);
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'DiaryEntries.docx';
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
  <div className="backdrop-blur-lg bg-white/80 rounded-2xl shadow-2xl p-8 max-h-[70vh] min-w-[340px] sm:min-w-[480px] md:min-w-[600px] lg:min-w-[700px] overflow-y-auto scrollbar-thin scrollbar-thumb-blue-200 scrollbar-track-white/40">
      <div className="flex items-center justify-center mb-8">
        <span className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-gradient-to-br from-blue-300 via-purple-300 to-pink-300 shadow-lg mr-4">
          <svg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 24 24' strokeWidth={2} stroke='currentColor' className='w-10 h-10 text-blue-700'>
            <path strokeLinecap='round' strokeLinejoin='round' d='M7 8h10M7 12h6m-6 4h10M5 6a2 2 0 012-2h10a2 2 0 012 2v14a2 2 0 01-2 2H7a2 2 0 01-2-2V6z' />
          </svg>
        </span>
        <h2 className="text-4xl font-semibold px-8 py-4 rounded-2xl bg-white/80 shadow text-blue-700 tracking-tight">Diary Entries</h2>
      </div>
      <button
        className="mb-4 flex items-center gap-1 bg-gradient-to-r from-blue-500 to-purple-500 text-white px-3 py-1 rounded-full font-bold shadow hover:from-blue-600 hover:to-purple-600 transition-all duration-200 text-sm active:scale-95 min-h-[28px] min-w-[44px]"
        onClick={downloadAllAsWord}
        style={{ touchAction: 'manipulation', lineHeight: 1 }}
      >
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4">
          <path strokeLinecap="round" strokeLinejoin="round" d="M12 16v-8m0 8l-4-4m4 4l4-4" />
        </svg>
        <span className="hidden sm:inline">Download All as Word</span>
      </button>
  <div className="flex gap-2 mb-4">
        <input
          type="text"
          className="p-3 border border-blue-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 bg-white/60 w-1/2"
          placeholder="Search..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <input
          type="text"
          className="p-3 border border-purple-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-400 bg-white/60"
          placeholder="Tag filter"
          value={tagFilter}
          onChange={e => setTagFilter(e.target.value)}
        />
        <input
          type="text"
          className="p-3 border border-pink-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-pink-400 bg-white/60"
          placeholder="Mood filter"
          value={moodFilter}
          onChange={e => setMoodFilter(e.target.value)}
        />
      </div>
      <div className="relative">
        <div className="border-l-4 border-blue-300 absolute h-full left-6 top-0"></div>
        <ul className="space-y-8">
            {filtered.length === 0 && (
              <li className="text-center text-gray-400 py-8 text-lg">No diary entries yet. Click "Create New Entry" to get started!</li>
            )}
            {filtered.map((entry, idx) => (
              <li key={entry.id} className="relative pl-16">
                <Link to={`/entry/${entry.id}`} className="block">
                  {/* Mood removed from list view */}
                  <div className="bg-white/80 backdrop-blur-lg rounded-xl shadow p-4">
                    <div className="flex justify-between items-center mb-2">
                      <div>
                        <span className="font-bold text-xl text-blue-700">{entry.title}</span>
                        <span className="ml-2 text-gray-500">{entry.date}</span>
                        {/* Tags removed from list view */}
                      </div>
                      <div className="flex gap-1 sm:gap-2">
                        <Link
                          to={`/entry/${entry.id}?edit=true`}
                          className="flex items-center gap-0.5 px-2 py-0.5 rounded-full bg-green-500 text-white font-semibold shadow hover:bg-green-600 transition-all duration-200 text-xs sm:text-sm min-w-[44px] min-h-[28px]"
                          onClick={e => e.stopPropagation()}
                          style={{ lineHeight: 1 }}
                        >
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-3 h-3 sm:w-4 sm:h-4">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M15.232 5.232l3.536 3.536M9 13l6.536-6.536a2 2 0 112.828 2.828L11.828 15H9v-2.828z" />
                          </svg>
                          <span className="hidden xs:inline">Edit</span>
                        </Link>
                        <button
                          className="flex items-center gap-0.5 px-2 py-0.5 rounded-full bg-red-500 text-white font-semibold shadow hover:bg-red-600 transition-all duration-200 text-xs sm:text-sm min-w-[44px] min-h-[28px]"
                          onClick={e => { e.preventDefault(); deleteDiaryEntry(entry.id); setEntries(entries.filter(e => e.id !== entry.id)); }}
                          style={{ touchAction: 'manipulation', lineHeight: 1 }}
                        >
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-3 h-3 sm:w-4 sm:h-4">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
                          </svg>
                          <span className="hidden xs:inline">Delete</span>
                        </button>
                      </div>
                    </div>
                    {entry.image && <img src={entry.image} alt="Diary" className="my-2 max-h-40 rounded-xl shadow" />}
                    <div className="prose max-w-none bg-white/60 p-3 rounded-lg" dangerouslySetInnerHTML={{ __html: entry.content }} />
                    <ExportButtons entry={entry} />
                  </div>
                </Link>
              </li>
            ))}
        </ul>
      </div>
    </div>
  );
}

export default DiaryList;
