import React, { useState } from 'react';
import DiaryEditor from './components/DiaryEditor';
import DiaryList from './components/DiaryList';
import BuyMeACoffee from './components/BuyMeACoffee';
import SideNote from './components/SideNote';
import MobileMenu from './components/MobileMenu';

function App() {
  const [refreshList, setRefreshList] = useState(0);
  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center py-8">
      <header className="mb-8 w-full max-w-xl mx-auto">
        <h1 className="text-3xl font-bold text-center text-blue-700 mb-2">DiaryMe</h1>
        <p className="text-center text-gray-500">Your private, simple diary app</p>
      </header>
      <main className="w-full max-w-xl mx-auto flex flex-col gap-6">
        <section className="bg-white rounded-xl shadow p-4">
          <DiaryEditor onSave={() => setRefreshList(r => r + 1)} />
        </section>
        <section className="bg-white rounded-xl shadow p-4">
          <DiaryList refresh={refreshList} />
        </section>
      </main>
      <div className="fixed bottom-4 left-4 flex gap-2">
        <BuyMeACoffee />
        <SideNote />
      </div>
    </div>
  );
}

export default App;
