import React, { useState } from 'react';
import { EditorContent, useEditor } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';
import Underline from '@tiptap/extension-underline';
import Link from '@tiptap/extension-link';
import Image from '@tiptap/extension-image';
// Removed code-block-lowlight and lowlight imports
import Color from '@tiptap/extension-color';
import TextStyle from '@tiptap/extension-text-style';
import { saveDiaryEntry } from '../db';

const moods = ['ðŸ˜Š', 'ðŸ˜¢', 'ðŸ˜¡', 'ðŸ˜±', 'ðŸ˜´', 'ðŸ˜'];

function DiaryEditor() {
  const [title, setTitle] = useState('');
  const [mood, setMood] = useState(moods[0]);
  const [tags, setTags] = useState('');
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [image, setImage] = useState(null);
  const editor = useEditor({
    extensions: [
      StarterKit,
      Underline,
      Link,
      Image,
  // Removed CodeBlockLowlight for compatibility
      Color,
      TextStyle,
    ],
    content: '',
  });

  // Save entry to IndexedDB
  const handleSave = async () => {
    if (!title || !editor) return;
    await saveDiaryEntry({
      title,
      content: editor.getHTML(),
      mood,
      tags: tags.split(',').map(t => t.trim()),
      date,
      image,
      created: Date.now(),
    });
    setTitle('');
    setTags('');
    setImage(null);
    editor.commands.setContent('');
  };

  // Handle image upload
  const handleImageChange = e => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setImage(reader.result);
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="backdrop-blur-lg bg-white/80 rounded-2xl shadow-2xl p-8 mb-6 border border-white/40">
      <h2 className="text-2xl font-bold mb-4 text-transparent bg-clip-text bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400">New Diary Entry</h2>
      <input
        type="text"
        className="w-full mb-3 p-3 border border-blue-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 bg-white/60"
        placeholder="Title"
        value={title}
        onChange={e => setTitle(e.target.value)}
      />
      <div className="flex gap-2 mb-3">
        {moods.map(m => (
          <button
            key={m}
            className={`text-2xl px-3 py-2 rounded-full shadow ${mood === m ? 'bg-blue-300 scale-110' : 'bg-gray-100'} transition-all duration-200`}
            onClick={() => setMood(m)}
            type="button"
          >
            {m}
          </button>
        ))}
      </div>
      <input
        type="text"
        className="w-full mb-3 p-3 border border-purple-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-400 bg-white/60"
        placeholder="Tags (comma separated)"
        value={tags}
        onChange={e => setTags(e.target.value)}
      />
      <input
        type="date"
        className="mb-3 p-3 border border-pink-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-pink-400 bg-white/60"
        value={date}
        onChange={e => setDate(e.target.value)}
      />
      <label className="flex items-center gap-2 cursor-pointer bg-white border-2 border-pink-400 text-pink-700 px-4 py-2 rounded-full font-bold shadow hover:bg-pink-50 hover:border-pink-600 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-pink-400 mb-3">
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
          <path strokeLinecap="round" strokeLinejoin="round" d="M3 16.5V19a2 2 0 002 2h14a2 2 0 002-2v-2.5M16.5 7.5l-4.5 4.5-4.5-4.5M12 12V3" />
        </svg>
        Upload Image
        <input
          type="file"
          accept="image/*"
          className="hidden"
          onChange={handleImageChange}
        />
      </label>
      {image && <img src={image} alt="Diary" className="mb-3 max-h-40 rounded-xl shadow" />}
      {/* Rich Text Toolbar */}
      {editor && (
        <div className="flex flex-wrap gap-2 mb-2">
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => editor.chain().focus().toggleBold().run()}>Bold</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => editor.chain().focus().toggleItalic().run()}>Italic</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => editor.chain().focus().toggleUnderline().run()}>Underline</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => editor.chain().focus().toggleStrike().run()}>Strike</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => editor.chain().focus().toggleHeading({ level: 1 }).run()}>H1</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => editor.chain().focus().toggleHeading({ level: 2 }).run()}>H2</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => editor.chain().focus().toggleHeading({ level: 3 }).run()}>H3</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => editor.chain().focus().toggleBulletList().run()}>Bullet List</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => editor.chain().focus().toggleOrderedList().run()}>Numbered List</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => editor.chain().focus().toggleBlockquote().run()}>Quote</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => editor.chain().focus().toggleCodeBlock().run()}>Code</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => {
            const url = prompt('Enter URL');
            if (url) editor.chain().focus().setLink({ href: url }).run();
          }}>Link</button>
          <button type="button" className="px-2 py-1 rounded bg-gray-100 hover:bg-blue-100" onClick={() => {
            const url = prompt('Image URL');
            if (url) editor.chain().focus().setImage({ src: url }).run();
          }}>Image</button>
          <input
            type="color"
            className="w-8 h-8 p-0 border-0 bg-transparent cursor-pointer"
            onChange={e => editor.chain().focus().setColor(e.target.value).run()}
            title="Text Color"
          />
        </div>
      )}
      <EditorContent editor={editor} className="border border-gray-200 rounded-lg p-3 mb-3 min-h-[120px] bg-white/60" />
      <button
        className="flex items-center gap-2 bg-white border-2 border-blue-400 text-blue-700 px-6 py-2 rounded-full font-bold shadow hover:bg-blue-50 hover:border-blue-600 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-400"
        onClick={handleSave}
      >
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
          <path strokeLinecap="round" strokeLinejoin="round" d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a2 2 0 01-2 2H7a2 2 0 01-2-2V7a2 2 0 012-2h4a2 2 0 012 2v1" />
        </svg>
        Save Entry
      </button>
    </div>
  );
}

export default DiaryEditor;
