import ExportButtons from './ExportButtons';
import BuyMeACoffee from './BuyMeACoffee';
import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getDiaryEntries } from '../db';
// ...existing code...

function EntryPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [entry, setEntry] = React.useState(null);

  React.useEffect(() => {
    getDiaryEntries().then(entries => {
      const found = entries.find(e => String(e.id) === String(id));
      setEntry(found || null);
    });
  }, [id]);

  if (!entry) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="bg-white rounded-xl shadow-xl p-8 text-center">
          <p className="text-lg text-gray-700">Entry not found.</p>
          <button className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-full" onClick={() => navigate(-1)}>Go Back</button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100">
      <div className="bg-white rounded-[3rem] shadow-2xl p-20 max-w-5xl w-full relative border-8 border-blue-300">
        <button
          className="absolute top-10 right-16 text-red-500 font-bold text-4xl hover:text-red-700"
          onClick={() => navigate(-1)}
        >Ã—</button>
        <h2 className="text-6xl font-extrabold mb-8 text-blue-700 text-center">{entry.title}</h2>
        <div className="mb-8 text-2xl text-gray-500 text-center">{entry.date} {entry.mood && <span className="ml-8 text-4xl">{entry.mood}</span>}</div>
        {entry.tags && entry.tags.length > 0 && (
          <div className="mb-8 text-xl bg-gradient-to-r from-blue-200 via-purple-200 to-pink-200 px-8 py-4 rounded-full text-gray-700 inline-block font-bold text-center">{entry.tags.join(', ')}</div>
        )}
        {entry.image && <img src={entry.image} alt="Diary" className="my-10 max-h-[40rem] rounded-3xl shadow-2xl mx-auto" />}
        <div className="prose prose-xl max-w-none bg-white/80 p-12 rounded-3xl border-2 border-gray-300 text-gray-900 text-center" dangerouslySetInnerHTML={{ __html: entry.content }} />
        <div className="flex flex-col items-center mt-8 gap-4">
          <ExportButtons entry={entry} />
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded-full font-bold shadow hover:bg-blue-600 transition-all duration-200"
            onClick={() => {
              navigator.clipboard.writeText(window.location.href);
              alert('Entry link copied! You can share it, but only you can view it on this device.');
            }}
          >
            Share Entry
          </button>
          <BuyMeACoffee />
        </div>
      </div>
    </div>
  );
}

export default EntryPage;
