import React, { useState } from 'react';

function SideNote() {
  const [open, setOpen] = useState(false);
  return (
    <div className="fixed top-6 right-6 z-50">
      <button
        className="bg-blue-500 text-white px-4 py-2 rounded-full shadow-lg hover:bg-blue-600 font-bold"
        onClick={() => setOpen(o => !o)}
      >
        {open ? 'Close Guide' : 'DiaryMe Quick Guide'}
      </button>
      {open && (
        <div className="mt-4 w-80 bg-white/90 backdrop-blur-lg border border-blue-200 rounded-2xl shadow-2xl p-6 text-gray-800 text-sm">
          <h3 className="text-xl font-bold mb-3 text-blue-700">DiaryMe Quick Guide</h3>
          <ol className="list-decimal ml-4 space-y-2">
            <li><span className="font-semibold">Welcome!</span> – Your private diary, saved only on your device.</li>
            <li><span className="font-semibold">New Entry</span> – Add a title, mood, tags, or images.</li>
            <li><span className="font-semibold">Auto-Save</span> – Your writing is saved automatically.</li>
            <li><span className="font-semibold">View Entries</span> – Browse by list or calendar. Filter by date, mood, or tags.</li>
            <li><span className="font-semibold">Download</span> – Export any entry as EPUB or PDF.</li>
            <li><span className="font-semibold">Support</span> – Use the “Buy Me a Coffee” button to help us.</li>
          </ol>
        </div>
      )}
    </div>
  );
}

export default SideNote;
