import React, { useState } from 'react';
import { EditorContent, useEditor } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';
import { saveDiaryEntry } from '../db';

const moods = ['ðŸ˜Š', 'ðŸ˜¢', 'ðŸ˜¡', 'ðŸ˜±', 'ðŸ˜´', 'ðŸ˜'];

function DiaryEditor() {
  const [title, setTitle] = useState('');
  const [mood, setMood] = useState(moods[0]);
  const [tags, setTags] = useState('');
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [image, setImage] = useState(null);
  const editor = useEditor({
    extensions: [StarterKit],
    content: '',
  });

  // Save entry to IndexedDB
  const handleSave = async () => {
    if (!title || !editor) return;
    await saveDiaryEntry({
      title,
      content: editor.getHTML(),
      mood,
      tags: tags.split(',').map(t => t.trim()),
      date,
      image,
      created: Date.now(),
    });
    setTitle('');
    setTags('');
    setImage(null);
    editor.commands.setContent('');
    alert('Diary entry saved locally!');
  };

  // Handle image upload
  const handleImageChange = e => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setImage(reader.result);
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow p-6 mb-6">
      <h2 className="text-xl font-semibold mb-4">New Diary Entry</h2>
      <input
        type="text"
        className="w-full mb-2 p-2 border rounded"
        placeholder="Title"
        value={title}
        onChange={e => setTitle(e.target.value)}
      />
      <div className="flex gap-2 mb-2">
        {moods.map(m => (
          <button
            key={m}
            className={`text-2xl px-2 py-1 rounded ${mood === m ? 'bg-blue-200' : ''}`}
            onClick={() => setMood(m)}
            type="button"
          >
            {m}
          </button>
        ))}
      </div>
      <input
        type="text"
        className="w-full mb-2 p-2 border rounded"
        placeholder="Tags (comma separated)"
        value={tags}
        onChange={e => setTags(e.target.value)}
      />
      <input
        type="date"
        className="mb-2 p-2 border rounded"
        value={date}
        onChange={e => setDate(e.target.value)}
      />
      <input
        type="file"
        accept="image/*"
        className="mb-2"
        onChange={handleImageChange}
      />
      {image && <img src={image} alt="Diary" className="mb-2 max-h-40 rounded" />}
      <EditorContent editor={editor} className="border rounded p-2 mb-2 min-h-[120px]" />
      <button
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        onClick={handleSave}
      >
        Save Entry
      </button>
    </div>
  );
}

export default DiaryEditor;
