import React, { useState } from 'react';
import PasswordGate from './PasswordGate';
import DiaryEditor from './components/DiaryEditor';
import DiaryList from './components/DiaryList';
import BuyMeACoffee from './components/BuyMeACoffee';
import SideNote from './components/SideNote';

function App() {
  const [refreshList, setRefreshList] = useState(0);
  const [showEditor, setShowEditor] = useState(false);
  const [unlocked, setUnlocked] = useState(() => sessionStorage.getItem('diary_unlocked') === 'true');
  if (!unlocked) {
    return <PasswordGate onUnlock={() => {
      setUnlocked(true);
      sessionStorage.setItem('diary_unlocked', 'true');
    }} />;
  }
  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-100 via-blue-100 to-purple-100 flex flex-col items-center py-4 px-2" style={{maxWidth: '100vw', overflowX: 'hidden'}}>
      <header className="mb-4 w-full max-w-full mx-auto px-2">
        <h1 className="text-3xl sm:text-4xl font-extrabold text-center text-pink-500 mb-2 font-cursive tracking-wide" style={{wordBreak: 'break-word'}}>
          DiaryMe
        </h1>
        <p className="text-center text-purple-400 font-cursive text-base sm:text-lg">Your private, cute diary app</p>
        <div className="w-full flex justify-center gap-2 mt-2 flex-wrap">
          <SideNote />
        </div>
      </header>
      <main className="w-full max-w-full mx-auto flex flex-col gap-4 px-0 sm:px-2">
        <section className="bg-white/90 rounded-3xl shadow-xl p-3 sm:p-4 flex flex-col items-center border-2 border-pink-200 w-full" style={{maxWidth: '100%', boxSizing: 'border-box'}}>
          {!showEditor && (
            <button
              className="mb-2 bg-gradient-to-r from-pink-400 via-blue-400 to-purple-400 text-white px-4 sm:px-6 py-2 rounded-full font-extrabold shadow hover:scale-105 transition-transform duration-200 text-lg font-cursive w-full sm:w-auto"
              onClick={() => setShowEditor(true)}
              style={{minWidth: 0, wordBreak: 'break-word'}}
            >
              + Create New Entry
            </button>
          )}
          {showEditor && (
            <div className="relative w-full">
              <button
                className="absolute top-2 right-2 bg-red-400 text-white rounded-full px-3 py-1 text-xs font-bold shadow hover:bg-red-500 z-10 font-cursive"
                onClick={() => setShowEditor(false)}
                aria-label="Close editor"
              >
                Ã—
              </button>
              <DiaryEditor
                onSave={() => {
                  setRefreshList(r => r + 1);
                  setShowEditor(false);
                }}
              />
            </div>
          )}
        </section>
        <section className="bg-white/90 rounded-3xl shadow-xl p-3 sm:p-4 border-2 border-blue-200 w-full" style={{maxWidth: '100%', boxSizing: 'border-box'}}>
          <DiaryList refresh={refreshList} />
        </section>
      </main>
      <div className="w-full flex justify-center gap-2 mt-6 mb-2 flex-wrap">
        <BuyMeACoffee />
      </div>
      <style>{`
        .font-cursive { font-family: 'Comic Sans MS', 'Comic Sans', cursive; }
        @media (max-width: 480px) {
          .rounded-3xl { border-radius: 1.5rem; }
          .shadow-xl { box-shadow: 0 4px 24px 0 rgba(0,0,0,0.08); }
          .p-3, .p-4 { padding: 1rem !important; }
          .mb-4 { margin-bottom: 1rem !important; }
          .w-full { width: 100% !important; }
          .max-w-full { max-width: 100vw !important; }
        }
      `}</style>
    </div>
  );
}

export default App;
