import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getDiaryEntries } from '../db';
// ...existing code...

function EntryPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [entry, setEntry] = React.useState(null);

  React.useEffect(() => {
    getDiaryEntries().then(entries => {
      const found = entries.find(e => String(e.id) === String(id));
      setEntry(found || null);
    });
  }, [id]);

  if (!entry) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="bg-white rounded-xl shadow-xl p-8 text-center">
          <p className="text-lg text-gray-700">Entry not found.</p>
          <button className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-full" onClick={() => navigate(-1)}>Go Back</button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100">
      <div className="bg-white rounded-3xl shadow-2xl p-12 max-w-3xl w-full relative border-4 border-blue-200">
        <button
          className="absolute top-6 right-8 text-red-500 font-bold text-2xl hover:text-red-700"
          onClick={() => navigate(-1)}
        >Ã—</button>
        <h2 className="text-4xl font-extrabold mb-4 text-blue-700">{entry.title}</h2>
        <div className="mb-4 text-lg text-gray-500">{entry.date} {entry.mood && <span className="ml-4 text-2xl">{entry.mood}</span>}</div>
        {entry.tags && entry.tags.length > 0 && (
          <div className="mb-4 text-base bg-gradient-to-r from-blue-200 via-purple-200 to-pink-200 px-4 py-2 rounded-full text-gray-700 inline-block font-semibold">{entry.tags.join(', ')}</div>
        )}
        {entry.image && <img src={entry.image} alt="Diary" className="my-6 max-h-96 rounded-2xl shadow-xl mx-auto" />}
        <div className="prose prose-lg max-w-none bg-white/60 p-6 rounded-xl border border-gray-200 text-gray-900" dangerouslySetInnerHTML={{ __html: entry.content }} />
      </div>
    </div>
  );
}

export default EntryPage;
