import React, { useState } from 'react';
import { supabase } from '../supabaseClient';

export default function Auth({ onAuth }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSignUp = async () => {
    setLoading(true);
    const { error } = await supabase.auth.signUp({ email, password });
    setLoading(false);
    if (error) setError(error.message);
    else onAuth();
  };

  const handleSignIn = async () => {
    setLoading(true);
    const { error } = await supabase.auth.signInWithPassword({ email, password });
    setLoading(false);
    if (error) setError(error.message);
    else onAuth();
  };

  return (
    <div className="flex flex-col gap-2 p-4 max-w-xs mx-auto">
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={e => setEmail(e.target.value)}
        className="border rounded p-2"
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={e => setPassword(e.target.value)}
        className="border rounded p-2"
      />
      {error && <div className="text-red-500 text-xs">{error}</div>}
      <button onClick={handleSignIn} disabled={loading} className="bg-blue-500 text-white rounded p-2">Sign In</button>
      <button onClick={handleSignUp} disabled={loading} className="bg-green-500 text-white rounded p-2">Sign Up</button>
    </div>
  );
}
