import React, { useState } from 'react';

function SideNote() {
  const [open, setOpen] = useState(true);
  return (
  <div className="fixed bottom-4 left-4 z-[9999] pointer-events-auto">
      <button
        className="bg-blue-500 text-white px-4 py-2 rounded-full shadow-lg hover:bg-blue-600 font-bold"
        onClick={() => setOpen(o => !o)}
        aria-label={open ? 'Close Guide' : 'Open DiaryMe Quick Guide'}
      >
        {open ? 'Close Guide' : 'DiaryMe Quick Guide'}
      </button>
      {open && (
        <div className="mt-2 w-64 bg-white/95 border border-blue-200 rounded-xl shadow-lg p-3 text-gray-800 text-xs z-[9999]">
          <h3 className="text-lg font-bold mb-2 text-blue-700">Quick Guide</h3>
          <ol className="list-decimal ml-3 space-y-1">
            <li><span className="font-semibold">Welcome!</span> – Your private diary, saved only on your device.</li>
            <li className="text-red-700 font-semibold">Privacy: Entries are <u>never uploaded</u>. <br/>Do <u>not</u> delete site data or you will lose your diary!</li>
            <li><span className="font-semibold">New Entry</span> – Add a title, mood, tags, or images.</li>
            <li><span className="font-semibold">Auto-Save</span> – Your writing is saved automatically.</li>
            <li><span className="font-semibold">View</span> – Browse by list or calendar. Filter by date, mood, or tags.</li>
            <li><span className="font-semibold">Download</span> – Export any entry as EPUB or PDF.</li>
            <li><span className="font-semibold">Support</span> – Use the “Buy Me a Coffee” button to help us.</li>
          </ol>
        </div>
      )}
    </div>
  );
}

export default SideNote;
