import React, { useState } from 'react';
import PasswordGate from './PasswordGate';
import DiaryEditor from './components/DiaryEditor';
import DiaryList from './components/DiaryList';
import BuyMeACoffee from './components/BuyMeACoffee';
import SideNote from './components/SideNote';

function App() {
  const [refreshList, setRefreshList] = useState(0);
  const [showEditor, setShowEditor] = useState(false);
  const [unlocked, setUnlocked] = useState(() => sessionStorage.getItem('diary_unlocked') === 'true');
  if (!unlocked) {
    return <PasswordGate onUnlock={() => {
      setUnlocked(true);
      sessionStorage.setItem('diary_unlocked', 'true');
    }} />;
  }
  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center py-8">
      <header className="mb-8 w-full max-w-xl mx-auto">
        <h1 className="text-3xl font-bold text-center text-blue-700 mb-2">DiaryMe</h1>
        <p className="text-center text-gray-500">Your private, simple diary app</p>
      </header>
      <main className="w-full max-w-xl mx-auto flex flex-col gap-6">
        <section className="bg-white rounded-xl shadow p-4 flex flex-col items-center">
          {!showEditor && (
            <button
              className="mb-2 bg-green-600 text-white px-6 py-2 rounded-full font-bold shadow hover:bg-green-700 transition-all duration-200 text-lg"
              onClick={() => setShowEditor(true)}
            >
              + Create New Entry
            </button>
          )}
          {showEditor && (
            <div className="relative w-full">
              <button
                className="absolute top-2 right-2 bg-red-500 text-white rounded-full px-3 py-1 text-xs font-bold shadow hover:bg-red-600 z-10"
                onClick={() => setShowEditor(false)}
                aria-label="Close editor"
              >
                Ã—
              </button>
              <DiaryEditor
                onSave={() => {
                  setRefreshList(r => r + 1);
                  setShowEditor(false);
                }}
              />
            </div>
          )}
        </section>
        <section className="bg-white rounded-xl shadow p-4">
          <DiaryList refresh={refreshList} />
        </section>
      </main>
      <div className="fixed bottom-4 left-4 flex gap-2">
        <BuyMeACoffee />
        <SideNote />
      </div>
    </div>
  );
}

export default App;
