import React from 'react';

const BuyMeACoffee = () => (
  <a
    href="https://hellomydude.gumroad.com/coffee"
    target="_blank"
    rel="noopener noreferrer"
    className="fixed bottom-2 right-2 z-50 bg-yellow-400 hover:bg-yellow-500 text-white font-bold py-1.5 px-2 rounded-full shadow-lg transition-all duration-300 flex items-center gap-1 text-xs sm:text-base sm:py-3 sm:px-6"
    style={{ boxShadow: '0 4px 24px rgba(255,193,7,0.3)' }}
  >
    <span role="img" aria-label="coffee">â˜•</span> Buy Me a Coffee
  </a>
);

export default BuyMeACoffee;
