import React, { useState } from "react";

function hash(str) {
  // Simple hash for demo (not cryptographically secure)
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    hash = ((hash << 5) - hash) + str.charCodeAt(i);
    hash |= 0;
  }
  return hash.toString();
}

export default function PasswordGate({ onUnlock }) {
  const [username, setUsername] = useState("");
  const [step, setStep] = useState(null);
  const [pw, setPw] = useState("");
  const [error, setError] = useState("");

  // Check if user exists when username changes
  React.useEffect(() => {
    if (!username) {
      setStep(null);
      return;
    }
    const userHash = localStorage.getItem("diary_pw_hash_" + username);
    setStep(userHash ? "enter" : "set");
  }, [username]);

  function handleSet(e) {
    e.preventDefault();
    if (pw.length < 4) {
      setError("Password must be at least 4 characters.");
      return;
    }
  localStorage.setItem("diary_pw_hash_" + username, hash(pw));
  setStep("enter");
  setPw("");
  setError("");
  }

  function handleEnter(e) {
    e.preventDefault();
    const stored = localStorage.getItem("diary_pw_hash_" + username);
    if (hash(pw) === stored) {
      setError("");
      sessionStorage.setItem("diary_user", username);
      onUnlock();
    } else {
      setError("Incorrect password.");
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form className="bg-white rounded shadow p-8 w-full max-w-sm mx-auto" onSubmit={step === "set" ? handleSet : handleEnter}>
        <h2 className="text-2xl font-bold mb-4 text-blue-700 text-center">
          {step === null ? "Enter Username" : step === "set" ? "Set a Password" : "Enter Password"}
        </h2>
        <input
          type="text"
          className="w-full p-2 border rounded mb-4"
          placeholder="Username"
          value={username}
          onChange={e => setUsername(e.target.value)}
          autoFocus
        />
        {step && (
          <input
            type="password"
            className="w-full p-2 border rounded mb-4"
            placeholder="Password"
            value={pw}
            onChange={e => setPw(e.target.value)}
          />
        )}
        {error && <div className="text-red-500 mb-2 text-center">{error}</div>}
        <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded font-bold" disabled={!username || (step && !pw)}>
          {step === null ? "Next" : step === "set" ? "Set Password" : "Unlock"}
        </button>
      </form>
    </div>
  );
}
