import React, { useState } from 'react';
import DiaryEditor from './components/DiaryEditor';
import DiaryList from './components/DiaryList';
import BuyMeACoffee from './components/BuyMeACoffee';
import SideNote from './components/SideNote';

function App() {
  const [refreshList, setRefreshList] = useState(0);
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100 flex flex-col items-center justify-center">
      <BuyMeACoffee />
      <SideNote />
      <div className="w-full max-w-5xl px-4 py-10">
        <div className="backdrop-blur-lg bg-white/70 rounded-3xl shadow-2xl p-8 mb-8 border border-white/40">
          <h1 className="text-4xl font-extrabold mb-2 text-center text-transparent bg-clip-text bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 drop-shadow-lg">diaryme.top</h1>
          <p className="text-lg font-semibold text-center text-blue-700 mb-6">DiaryMe.top â€“ Keep Your Thoughts on Top</p>
          <DiaryEditor onSave={() => setRefreshList(r => r + 1)} />
        </div>
        <div className="w-full">
          <div className="backdrop-blur-lg bg-white/60 rounded-2xl shadow-xl p-6 border border-white/30 w-full">
            <DiaryList refresh={refreshList} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
