import React, { useState } from 'react';
import { supabase } from '../supabaseClient';

export default function Auth({ onAuth }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSignUp = async () => {
    setLoading(true);
    const { error } = await supabase.auth.signUp({ email, password });
    setLoading(false);
    if (error) setError(error.message);
    else onAuth();
  };

  const handleSignIn = async () => {
    setLoading(true);
    const { error } = await supabase.auth.signInWithPassword({ email, password });
    setLoading(false);
    if (error) setError(error.message);
    else onAuth();
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-pink-200 via-blue-100 to-purple-200 py-8 px-2">
      <div className="bg-white/95 rounded-3xl shadow-2xl p-6 max-w-xs w-full flex flex-col items-center border-2 border-pink-200" style={{boxShadow:'0 8px 32px rgba(200,150,255,0.18)'}}>
        <span className="inline-flex items-center justify-center w-20 h-20 rounded-full bg-gradient-to-br from-pink-300 via-blue-300 to-purple-300 shadow-lg mb-4 animate-bounce-slow">
          <span role="img" aria-label="diary" className="text-4xl">ðŸ¦„</span>
        </span>
        <h2 className="text-3xl font-extrabold text-pink-500 mb-2 font-cursive tracking-wide text-center drop-shadow">Welcome to DiaryMe!</h2>
        <p className="text-center text-purple-400 font-cursive text-base mb-4">Sign up or log in to save your diary and switch devices.</p>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          className="border-2 border-blue-200 rounded-full p-3 mb-2 w-full focus:outline-none focus:ring-2 focus:ring-pink-300 bg-white/60 text-blue-700 placeholder-pink-300 text-base"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          className="border-2 border-blue-200 rounded-full p-3 mb-2 w-full focus:outline-none focus:ring-2 focus:ring-pink-300 bg-white/60 text-blue-700 placeholder-pink-300 text-base"
        />
        {error && <div className="text-red-500 text-xs mb-2 text-center">{error}</div>}
        <button onClick={handleSignIn} disabled={loading} className="bg-gradient-to-r from-blue-400 to-purple-400 text-white rounded-full p-3 w-full font-bold shadow hover:scale-105 transition-transform duration-200 mb-2 text-base">Sign In</button>
        <button onClick={handleSignUp} disabled={loading} className="bg-gradient-to-r from-pink-400 to-purple-400 text-white rounded-full p-3 w-full font-bold shadow hover:scale-105 transition-transform duration-200 text-base">Sign Up</button>
      </div>
      <style>{`
        .animate-bounce-slow {
          animation: bounce 2.5s infinite;
        }
        @keyframes bounce {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-12px); }
        }
      `}</style>
    </div>
  );
}
