import React from 'react';
import JSZip from 'jszip';
import html2pdf from 'html2pdf.js';

// Helper to export as EPUB
function exportEPUB(entry) {
  const zip = new JSZip();
  const htmlContent = `<h1>${entry.title}</h1><div>${entry.content}</div>`;
  zip.file('index.html', htmlContent);
  zip.generateAsync({ type: 'blob' }).then(blob => {
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${entry.title || 'diary'}.epub.zip`;
    a.click();
    URL.revokeObjectURL(url);
  });
}

// Helper to export as PDF
function exportPDF(entry) {
  const element = document.createElement('div');
  element.innerHTML = `<h1>${entry.title}</h1><div>${entry.content}</div>`;
  html2pdf().from(element).set({ filename: `${entry.title || 'diary'}.pdf` }).save();
}

const ExportButtons = ({ entry }) => (
  <div className="flex gap-2 mt-2 items-center">
    <button
      className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
      onClick={() => exportEPUB(entry)}
    >
      Download as EPUB
    </button>
    <button
      className="bg-purple-500 text-white px-3 py-1 rounded hover:bg-purple-600"
      onClick={() => exportPDF(entry)}
    >
      Download as PDF
    </button>
    <a
      href="https://hellomydude.gumroad.com/coffee"
      target="_blank"
      rel="noopener noreferrer"
      className="ml-2 bg-yellow-400 hover:bg-yellow-500 text-white font-bold px-3 py-1 rounded-full shadow transition-all duration-300 flex items-center gap-1"
      style={{ boxShadow: '0 2px 8px rgba(255,193,7,0.2)' }}
    >
      <span role="img" aria-label="coffee">â˜•</span> Buy Me a Coffee
    </a>
  </div>
);

export default ExportButtons;
