import React, { useState } from 'react';
import { saveDiaryEntry } from '../db';

const moods = [
  { icon: 'ðŸ˜Š', label: 'Happy' },
  { icon: 'ðŸ˜¢', label: 'Sad' },
  { icon: 'ðŸ˜¡', label: 'Angry' },
  { icon: 'ðŸ˜±', label: 'Surprised' },
  { icon: 'ðŸ˜´', label: 'Tired' },
  { icon: 'ðŸ˜', label: 'Love' }
];

function DiaryEditor(props) {
  const initial = props.initialEntry || {};
  const [title, setTitle] = useState(initial.title || '');
  const [mood, setMood] = useState(initial.mood || moods[0].icon);
  const [tags, setTags] = useState(initial.tags ? initial.tags.join(', ') : '');
  const [date, setDate] = useState(initial.date || new Date().toISOString().slice(0, 10));
  const [image, setImage] = useState(initial.image || null);
  const [content, setContent] = useState(initial.content || '');
  const [error, setError] = useState('');

  const handleSave = async () => {
    if (!title || !content) {
      setError('Please fill in both Title and Content to save your entry.');
      return;
    }
    setError('');
    const entryData = {
      title,
      content,
      mood,
      tags: tags.split(',').map(t => t.trim()),
      date,
      image,
      created: initial.created || Date.now(),
    };
    if (initial.id) {
      // Update existing entry
      entryData.id = initial.id;
      await import('../db').then(mod => mod.db.entries.put(entryData));
    } else {
      // Add new entry
      await saveDiaryEntry(entryData);
    }
    if (typeof props.onSave === 'function') props.onSave();
  };

  const handleImageChange = e => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setImage(reader.result);
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="backdrop-blur-lg bg-white/80 rounded-2xl shadow-2xl p-8 mb-6 border border-white/40">
  <div className="flex items-center justify-center mb-8">
    <span className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-gradient-to-br from-blue-300 via-purple-300 to-pink-300 shadow-lg mr-4">
      <svg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 24 24' strokeWidth={2} stroke='currentColor' className='w-10 h-10 text-blue-700'>
        <path strokeLinecap='round' strokeLinejoin='round' d='M12 6v6m0 0v6m0-6h6m-6 0H6' />
      </svg>
    </span>
    <h2 className="text-4xl font-semibold px-8 py-4 rounded-2xl bg-white/80 shadow text-blue-700 tracking-tight">New Diary Entry</h2>
  </div>
  {error && <p className="mb-4 text-sm text-red-600 font-semibold">{error}</p>}
      <input
        type="text"
        className="w-full mb-3 p-3 border border-blue-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 bg-white/60"
        placeholder="Title"
        value={title}
        onChange={e => setTitle(e.target.value)}
      />
      <div className="flex gap-2 mb-3">
        {moods.map(m => (
          <button
            key={m.icon}
            className={`flex flex-col items-center text-2xl px-3 py-2 rounded-full shadow ${mood === m.icon ? 'bg-blue-300 scale-110' : 'bg-gray-100'} transition-all duration-200`}
            onClick={() => setMood(m.icon)}
            type="button"
          >
            <span>{m.icon}</span>
            <span className="text-xs font-semibold mt-1 text-blue-700">{m.label}</span>
          </button>
        ))}
      </div>
      <input
        type="text"
        className="w-full mb-3 p-3 border border-purple-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-400 bg-white/60"
        placeholder="Tags (comma separated)"
        value={tags}
        onChange={e => setTags(e.target.value)}
      />
      <input
        type="date"
        className="mb-3 p-3 border border-pink-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-pink-400 bg-white/60"
        value={date}
        onChange={e => setDate(e.target.value)}
      />
      <label className="flex items-center gap-2 cursor-pointer bg-white border-2 border-pink-400 text-pink-700 px-4 py-2 rounded-full font-bold shadow hover:bg-pink-50 hover:border-pink-600 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-pink-400 mb-3">
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
          <path strokeLinecap="round" strokeLinejoin="round" d="M3 16.5V19a2 2 0 002 2h14a2 2 0 002-2v-2.5M16.5 7.5l-4.5 4.5-4.5-4.5M12 12V3" />
        </svg>
        Upload Image
        <input
          type="file"
          accept="image/*"
          className="hidden"
          onChange={handleImageChange}
        />
      </label>
      {image && <img src={image} alt="Diary" className="mb-3 max-h-40 rounded-xl shadow" />}
      {/* Markdown textarea editor */}
      <textarea
        className="border border-gray-200 rounded-lg p-3 mb-3 min-h-[120px] bg-white/60 w-full font-mono resize-vertical"
        placeholder="Write your diary entry in Markdown..."
        value={content}
        onChange={e => setContent(e.target.value)}
      />
      <button
        className="flex items-center gap-2 bg-white border-2 border-blue-400 text-blue-700 px-6 py-2 rounded-full font-bold shadow hover:bg-blue-50 hover:border-blue-600 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-400"
        onClick={handleSave}
      >
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
          <path strokeLinecap="round" strokeLinejoin="round" d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a2 2 0 01-2 2H7a2 2 0 01-2-2V7a2 2 0 012-2h4a2 2 0 012 2v1" />
        </svg>
        Save Entry
      </button>
    </div>
  );
}

export default DiaryEditor;
